graphql.query:
query {
  referrals(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      referrals {
          id,
          referee {
              id,
              firstName,
              lastName,
              referralCode
          },
          isRedemmed,
          createdAt,
          user {
              firstName,
              lastName,
              email
          }
      }
      total
  }
}

query {
  users(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      users {
          id,
          email,
          firstName,
          lastName,
          mobilePhone,
          organisation {
              id
          },
          role {
              id
          },
          referrals {
            id,
            user {
                id,
                firstName
            },
            referee {
                id,
                firstName,
                referralCode
            },
            isRedemmed,
            createdAt
          }
