  }
}

query {
  userTasks(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      userTasks {
          id,
          isComplete,
          status,
          task {
              id,
              title,
              description,
          },
          user {
              id,
              firstName,
              lastName,
              email
          }
          userSubtasks {
              id,
              isComplete,
              status
          },
          createdAt
      }
      total
  }
}

mutation userTaskCreate {
    userTaskCreate(
        input: {
            taskID: "45690607-4ff8-4dbd-ba52-6441d0003e36"
        }
    ) {
        id,
        isComplete,
        status,
        task {
            id,
            code,
            title,
            description
        },
        user {
            id,
            firstName,
            lastName,
            email
        },
        userSubtasks {
            id
        }
        createdAt
    }
}

query {
  tasks(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      tasks {
          id,
          code,
          title,
          description,
          isTimeBound,
          isPeopleBound,
          isProductRelevant,
          isFinal,
          finishDate,
          maximumPeople,
          skuID,
          subtasks {
              id,
              title,
              description
          },
          createdAt
      }
      total
  }
}

query task {
  task(id: "45690607-4ff8-4dbd-ba52-6441d0003e36") {
    id,
    title,
    description,
    loyaltyPoints,
    isTimeBound,
    isPeopleBound,
    isProductRelevant,
    finishDate,
    maximumPeople,
    skuID,
    subtasks {
        id,
        title,
        description
    }
    createdAt
  }
}

mutation taskCreate {
    taskCreate(
        input: {
            title: "Task 1"
            description: "Task One"
            loyaltyPoints: 50
            isTimeBound: true
            isPeopleBound: true
            isProductRelevant: false
            finishDate: "2021-04-06T15:04:05Z"
            maximumPeople: 10
            subtasks: [
                {
                    title: "Subtask 1"
                    description: "Subtask One"
                },
                {
                    title: "Subtask 2"
                    description: "Subtask Two"
                }
            ]
        }
    ) {
        id,
        code,
        title,
        description,
        loyaltyPoints,
        isTimeBound,
        isPeopleBound,
        isProductRelevant,
        isFinal,
        finishDate,
        maximumPeople,
        skuID,
        subtasks {
            id,
            title,
            description
        }
    }
}

