graphql.query:
query {
  referrals(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      referrals {
          id,
          code,
          referee {
              id,
              firstName,
              lastName,
              referralCode
          },
          isRedemmed,
          createdAt,
          user {
              firstName,
              lastName,
              email
          }
      }
      total
  }
}

