graphql.query:
query {
  referrals(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      referrals {
          id,
          code,
          referee {
              id,
              firstName,
              lastName,
              referralCode
          },
          isRedemmed,
          createdAt,
          user {
              firstName,
              lastName,
              email
          }
      }
      total
  }
}

query {
  users(
    search: {
      sortBy: DateCreated,
      sortDir: Ascending
    },
    limit: 100,
    offset: 0
  ) {
      users {
          id,
          email,
          firstName,
          lastName,
          mobilePhone,
          walletPoints,
          referralCode,
          organisation {
              id
          },
          role {
              id
          },
          referrals {
            id,
